mapper.select:
SELECT COUNT(*)
FROM board
WHERE title LIKE #{query}

SELECT board.id, title, contents
FROM board
         LEFT JOIN webdb.user u ON board.user_id = u.id
WHERE board.id = #{postId}
  AND user_id = #{userId};

mapper.update:
UPDATE board
SET o_no = o_no + 1
WHERE g_no = #{groupNo}
  AND o_no >= #{orderNo};

mapper.insert:
INSERT INTO board
(title, contents, user_id, o_no, g_no, depth)
SELECT #{title}, #{contents}, #{userId}


        , #{orderNo}, #{groupNo}, #{depth}


        , 0, MAX(g_no) + 1, 0


FROM board
LIMIT 0, 1;

mapper.update:
UPDATE board
SET title    = #{title},
    contents = #{contents}
WHERE id = #{id}
  AND user_id = #{userId};

mapper.delete:
DELETE
FROM board
WHERE id = #{postId}
  AND user_id = #{userId};

mapper.update:
UPDATE board
SET hit = hit + 1
WHERE id = #{postId};
